INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('6906ce20fabf0ea0252ede5e', 'Dan Wake', 'Traditional Hausa bean dumplings served with spicy sauce. Chewy, nutritious and uniquely Northern Nigerian.', 'nigerian', 'snack', '[{"item":"Dried beans (black-eyed peas)","quantity":"3 cups","estimated_cost":900},{"item":"Potash (kanwa)","quantity":"2 tsp","estimated_cost":100},{"item":"Tamarind","quantity":"1/2 cup","estimated_cost":200},{"item":"Tomatoes","quantity":"4 medium","estimated_cost":400},{"item":"Red bell pepper","quantity":"2 medium","estimated_cost":300},{"item":"Scotch bonnet pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Onions","quantity":"2 large","estimated_cost":200},{"item":"Vegetable oil","quantity":"1/4 cup","estimated_cost":200},{"item":"Ginger (grated)","quantity":"2 tsp","estimated_cost":100},{"item":"Garlic (minced)","quantity":"2 cloves","estimated_cost":50},{"item":"Seasoning cubes","quantity":"2","estimated_cost":100},{"item":"Salt","quantity":"to taste","estimated_cost":20}]', '["Soak beans overnight, then peel off skins","Grind beans into a smooth paste","Dissolve potash in water and filter","Mix bean paste with potash solution thoroughly","Allow to rest for 30 minutes","Cut mixture into small cubes or diamond shapes","Boil water in a large pot","Drop the dumplings into boiling water","Cook for 45-60 minutes until firm","Remove and soak in cold water","For sauce: blend tomatoes, peppers, onions, ginger and garlic","Heat oil in a pan and fry the blended mixture","Add seasoning, salt and cook for 10-15 minutes","Soak tamarind in warm water, extract juice","Add tamarind juice to the sauce","Drain dan wake and cut into bite-sized pieces","Toss dan wake in the spicy sauce","Serve garnished with sliced onions and cucumbers"]', 45, 75, 6, 2720, '{"calories":280,"protein":18,"carbs":38,"fats":6,"fiber":10}', '["traditional","hausa-cuisine","unique","high-protein","vegan"]', NULL, '2025-11-02T09:21:04.630Z', '2025-11-02T09:21:04.630Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('6906ce20fabf0ea0252ede5f', 'Masa (Rice Cakes)', 'Fluffy Hausa rice pancakes. Sweet or savory, perfect for breakfast or snacks. Similar to pancakes but uniquely Nigerian.', 'nigerian', 'breakfast', '[{"item":"Rice","quantity":"3 cups","estimated_cost":900},{"item":"Active dry yeast","quantity":"2 tsp","estimated_cost":200},{"item":"Sugar","quantity":"3 tbsp","estimated_cost":100},{"item":"Potash (kanwa, optional)","quantity":"1/4 tsp","estimated_cost":50},{"item":"Salt","quantity":"1/2 tsp","estimated_cost":20},{"item":"Warm water","quantity":"as needed","estimated_cost":0},{"item":"Vegetable oil for frying","quantity":"as needed","estimated_cost":300}]', '["Soak rice in water overnight or for at least 6 hours","Drain and rinse the rice","Blend rice with a little water into smooth batter","The batter should be thick but pourable","Dissolve yeast and sugar in warm water","Add yeast mixture to rice batter","If using potash, dissolve in water and add to batter","Add salt and mix well","Cover and allow to ferment for 2-4 hours (or overnight)","Batter will rise and have bubbles","Heat masa pan or small frying pan","Brush with oil","Pour small amounts of batter into each mold","Cook on medium heat for 2-3 minutes","Flip and cook other side until golden brown","Remove and drain on paper towels","Serve warm with sugar, honey or pepper sauce"]', 20, 30, 8, 1570, '{"calories":220,"protein":5,"carbs":42,"fats":4,"fiber":1}', '["breakfast","traditional","hausa-cuisine","fermented","versatile"]', NULL, '2025-11-02T09:21:04.630Z', '2025-11-02T09:21:04.630Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('6906ce20fabf0ea0252ede60', 'Miyan Zogale (Moringa Soup)', 'Nutritious Northern Nigerian soup made with moringa leaves. Light, healthy and packed with vitamins.', 'nigerian', 'lunch', '[{"item":"Fresh moringa leaves (zogale)","quantity":"4 cups","estimated_cost":400},{"item":"Beef","quantity":"500g","estimated_cost":3000},{"item":"Stockfish","quantity":"150g","estimated_cost":800},{"item":"Palm oil or vegetable oil","quantity":"1/2 cup","estimated_cost":400},{"item":"Locust beans (dadawa)","quantity":"2 tbsp","estimated_cost":200},{"item":"Tomatoes","quantity":"4 medium","estimated_cost":400},{"item":"Onions","quantity":"2 medium","estimated_cost":200},{"item":"Scotch bonnet pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Baobab powder (kuka, optional)","quantity":"2 tbsp","estimated_cost":300},{"item":"Ground peanuts or groundnut paste","quantity":"1/2 cup","estimated_cost":400},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150},{"item":"Salt","quantity":"to taste","estimated_cost":20}]', '["Pick and wash moringa leaves thoroughly","Blanch leaves in boiling water for 2 minutes, drain","Season and cook beef until tender","Soak and cook stockfish separately","Blend tomatoes, peppers and half the onions","Heat oil in a pot","Fry locust beans for 2 minutes","Add blended tomatoes and fry for 10 minutes","Add ground peanuts/groundnut paste and stir","Add meat stock gradually while stirring","Add beef, stockfish and seasoning","Simmer for 10 minutes","Add blanched moringa leaves","Add remaining sliced onions","If using baobab powder, add and stir well","Cook for 5 more minutes","Adjust seasoning to taste","Serve hot with tuwo shinkafa, fufu or eba"]', 25, 40, 6, 6420, '{"calories":380,"protein":28,"carbs":15,"fats":24,"fiber":5}', '["traditional","northern-cuisine","healthy","nutrient-dense","moringa"]', NULL, '2025-11-02T09:21:04.630Z', '2025-11-02T09:21:04.630Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('6906ce20fabf0ea0252ede61', 'Suya (Spiced Grilled Meat)', 'Nigeria''s favorite street food! Thinly sliced beef seasoned with spicy peanut spice mix and grilled to perfection.', 'nigerian', 'snack', '[{"item":"Beef (sirloin or flank steak)","quantity":"1kg","estimated_cost":6000},{"item":"Groundnuts (roasted)","quantity":"1 cup","estimated_cost":400},{"item":"Ginger powder","quantity":"2 tsp","estimated_cost":100},{"item":"Garlic powder","quantity":"1 tsp","estimated_cost":100},{"item":"Cayenne pepper","quantity":"2 tbsp","estimated_cost":150},{"item":"Paprika","quantity":"2 tbsp","estimated_cost":200},{"item":"Onion powder","quantity":"1 tbsp","estimated_cost":100},{"item":"Seasoning cubes (crushed)","quantity":"3","estimated_cost":150},{"item":"Salt","quantity":"1 tsp","estimated_cost":20},{"item":"Vegetable oil","quantity":"2 tbsp","estimated_cost":100},{"item":"Fresh onions (sliced)","quantity":"2 medium","estimated_cost":200},{"item":"Tomatoes (sliced)","quantity":"2 medium","estimated_cost":200},{"item":"Cabbage (shredded, for serving)","quantity":"1 small","estimated_cost":200}]', '["Grind roasted groundnuts into fine powder (yaji spice base)","Mix groundnut powder with ginger, garlic, cayenne, paprika, onion powder","Add crushed seasoning cubes and salt to create suya spice","Slice beef into thin strips (about 1/4 inch thick)","Rub beef strips with oil","Coat generously with suya spice mixture","Let marinate for at least 30 minutes (or overnight for best flavor)","Thread meat onto skewers","Preheat grill or oven to high heat","Grill meat for 3-4 minutes per side until charred and cooked","Brush with oil during grilling to prevent drying","Remove from heat when cooked but still juicy","Sprinkle with additional suya spice","Serve hot with sliced onions, tomatoes, cabbage","Can also serve with fried yam or plantain"]', 40, 15, 6, 7920, '{"calories":420,"protein":42,"carbs":8,"fats":24,"fiber":3}', '["street-food","grilled","spicy","protein-rich","popular","party-favorite"]', NULL, '2025-11-02T09:21:04.630Z', '2025-11-02T09:21:04.630Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('6906cc9cc2fa6a1e605f2f85', 'Nkwobi', 'Spicy cow foot delicacy cooked in a rich palm oil and utazi sauce. A popular Igbo appetizer perfect for sharing.', 'nigerian', 'snack', '[{"item":"Cow foot (nkwobi)","quantity":"2kg","estimated_cost":4000},{"item":"Edible potash (akanwu/kaun)","quantity":"1 tsp","estimated_cost":100},{"item":"Palm oil","quantity":"1 cup","estimated_cost":800},{"item":"Utazi leaves (sliced)","quantity":"1 cup","estimated_cost":200},{"item":"Ehuru (calabash nutmeg)","quantity":"4 pieces","estimated_cost":200},{"item":"Ehu seeds (African nutmeg)","quantity":"2 pieces","estimated_cost":100},{"item":"Uziza seeds (ground)","quantity":"2 tbsp","estimated_cost":300},{"item":"Crayfish (ground)","quantity":"4 tbsp","estimated_cost":600},{"item":"Habanero pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Onions","quantity":"2 medium","estimated_cost":200},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150},{"item":"Salt","quantity":"to taste","estimated_cost":20}]', '["Clean cow foot thoroughly and cut into bite-sized pieces","Season with salt, onions and seasoning cubes","Cook in pressure cooker for 1-2 hours until very tender","Drain and reserve about 1/2 cup of stock","Roast and grind ehuru and ehu seeds","Dissolve potash in a little water and filter","In a bowl, mix palm oil with the dissolved potash solution","Stir vigorously until palm oil thickens and turns yellow","Add ground ehuru, ehu, uziza seeds, and crayfish","Add habanero pepper (whole or sliced)","Add reserved stock gradually while mixing to desired consistency","Add seasoning to taste","Place cooked cow foot in a pot on very low heat","Pour the prepared sauce over the meat","Toss gently to coat, heat for 2-3 minutes","Add sliced utazi leaves and sliced onions","Mix well and transfer to a wooden bowl","Serve hot with chilled drinks"]', 30, 120, 6, 6820, '{"calories":380,"protein":35,"carbs":8,"fats":24,"fiber":2}', '["traditional","igbo-cuisine","appetizer","special-occasion","protein-rich"]', NULL, '2025-11-02T09:14:36.002Z', '2025-11-02T09:14:36.002Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('6906cc9cc2fa6a1e605f2f86', 'Abacha (African Salad)', 'Refreshing cassava-based salad with fish, vegetables and spices. A beloved Igbo delicacy that''s perfect for any occasion.', 'nigerian', 'snack', '[{"item":"Abacha (African salad cassava)","quantity":"500g","estimated_cost":800},{"item":"Stockfish","quantity":"200g","estimated_cost":1000},{"item":"Dried fish","quantity":"150g","estimated_cost":800},{"item":"Garden eggs","quantity":"6 pieces","estimated_cost":300},{"item":"Ugba (oil bean seed)","quantity":"1 cup","estimated_cost":600},{"item":"Edible potash (akanwu)","quantity":"1 tsp","estimated_cost":100},{"item":"Palm oil","quantity":"3/4 cup","estimated_cost":600},{"item":"Utazi leaves (sliced)","quantity":"1/2 cup","estimated_cost":100},{"item":"Ehuru (ground)","quantity":"2 tsp","estimated_cost":100},{"item":"Crayfish (ground)","quantity":"4 tbsp","estimated_cost":600},{"item":"Onions","quantity":"2 medium","estimated_cost":200},{"item":"Pepper (habanero)","quantity":"2 pieces","estimated_cost":100},{"item":"Seasoning cubes","quantity":"2","estimated_cost":100},{"item":"Salt","quantity":"to taste","estimated_cost":20}]', '["Soak abacha in cold water for 5-10 minutes to soften","Drain and pour hot water over it, leave for 2 minutes","Drain again and set aside","Soak and cook stockfish until tender, shred into pieces","Debone dried fish and break into chunks","Slice garden eggs","Dissolve potash in 2 tbsp water and filter","In a large bowl, mix palm oil with dissolved potash","Stir until oil thickens and turns yellow","Add ground ehuru, crayfish, pepper, and seasoning","Add the softened abacha to the sauce","Mix well ensuring abacha is well coated","Add ugba, stockfish, dried fish","Add sliced onions, garden eggs, and utazi leaves","Toss everything together gently","Adjust seasoning to taste","Garnish with extra utazi leaves and onion rings","Serve at room temperature with drinks"]', 30, 20, 6, 5420, '{"calories":320,"protein":22,"carbs":28,"fats":16,"fiber":4}', '["traditional","igbo-cuisine","refreshing","party-favorite","unique"]', NULL, '2025-11-02T09:14:36.002Z', '2025-11-02T09:14:36.002Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('6906cc9cc2fa6a1e605f2f87', 'Ukwa (Breadfruit Porridge)', 'Nutritious African breadfruit cooked in a rich palm oil sauce. A delicious traditional Igbo dish that''s both filling and healthy.', 'nigerian', 'lunch', '[{"item":"Ukwa (breadfruit seeds)","quantity":"4 cups","estimated_cost":2000},{"item":"Stockfish","quantity":"150g","estimated_cost":800},{"item":"Dried fish","quantity":"100g","estimated_cost":500},{"item":"Palm oil","quantity":"3/4 cup","estimated_cost":600},{"item":"Potash (edible)","quantity":"1/2 tsp","estimated_cost":50},{"item":"Crayfish (ground)","quantity":"3 tbsp","estimated_cost":450},{"item":"Pepper (habanero)","quantity":"3 pieces","estimated_cost":150},{"item":"Onions","quantity":"1 large","estimated_cost":100},{"item":"Uziza leaves (optional)","quantity":"1/2 cup sliced","estimated_cost":100},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150},{"item":"Salt","quantity":"to taste","estimated_cost":20}]', '["Wash ukwa (breadfruit) thoroughly","Soak and cook stockfish until tender","In a pressure cooker, add ukwa with plenty of water","Cook for 45-60 minutes until soft (or 2-3 hours in regular pot)","Drain excess water, leaving just a little","Dissolve potash in small amount of water and filter","Add palm oil to the cooked ukwa","Add dissolved potash solution and stir (thickens the sauce)","Add shredded stockfish, dried fish","Add ground crayfish, pepper, onions","Add seasoning cubes and salt","Simmer on low heat for 10-15 minutes, stirring occasionally","Add uziza leaves if using","Cook for 3 more minutes","Adjust seasoning to taste","Serve hot as a complete meal or with plantain"]', 15, 75, 6, 4920, '{"calories":380,"protein":16,"carbs":48,"fats":15,"fiber":8}', '["traditional","igbo-cuisine","nutritious","high-fiber","unique"]', NULL, '2025-11-02T09:14:36.002Z', '2025-11-02T09:14:36.002Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6ec44cae56206536830e', 'Banga Rice', 'Palm nut rice from the Niger Delta. Rich, flavorful and uniquely Nigerian.', 'nigerian', 'lunch', '[{"item":"Long grain rice","quantity":"4 cups","estimated_cost":1200},{"item":"Palm nut extract (banga)","quantity":"3 cups","estimated_cost":1200},{"item":"Stockfish","quantity":"150g","estimated_cost":800},{"item":"Dried fish","quantity":"100g","estimated_cost":500},{"item":"Beef or goat meat","quantity":"400g","estimated_cost":2400},{"item":"Banga spice","quantity":"2 tbsp","estimated_cost":300},{"item":"Scent leaves (sliced)","quantity":"1/2 cup","estimated_cost":100},{"item":"Onions","quantity":"1 large","estimated_cost":100},{"item":"Pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150},{"item":"Salt","quantity":"to taste","estimated_cost":20}]', '["Season and cook meat until tender","Soak and boil stockfish and dried fish","Wash rice thoroughly","In a large pot, combine banga extract with water (2 cups)","Add banga spice, pepper, onions and bring to boil","Add cooked meat, stockfish, dried fish and seasoning","Simmer for 10 minutes","Add washed rice and stir well","Cover and cook on low-medium heat for 25-30 minutes","Stir occasionally to prevent burning","Add scent leaves in last 5 minutes","Rice should be tender and liquid absorbed","Serve hot with salad or coleslaw"]', 20, 50, 6, 6920, '{"calories":480,"protein":24,"carbs":60,"fats":18,"fiber":3}', '["traditional","delta-cuisine","rich-flavor","unique","protein-rich"]', NULL, '2025-10-13T09:15:16.025Z', '2025-10-13T09:15:16.025Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6ec44cae562065368312', 'Asaro (Yam Porridge)', 'Rich, peppery yam porridge. Comfort food that''s creamy, filling and absolutely delicious.', 'nigerian', 'lunch', '[{"item":"White yam","quantity":"1.5kg","estimated_cost":1800},{"item":"Stockfish (optional)","quantity":"100g","estimated_cost":500},{"item":"Dried fish (optional)","quantity":"100g","estimated_cost":500},{"item":"Tomatoes","quantity":"4 medium","estimated_cost":400},{"item":"Red bell pepper","quantity":"2 large","estimated_cost":300},{"item":"Scotch bonnet pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Onions","quantity":"2 medium","estimated_cost":200},{"item":"Palm oil","quantity":"3/4 cup","estimated_cost":600},{"item":"Crayfish (ground)","quantity":"3 tbsp","estimated_cost":450},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150},{"item":"Salt","quantity":"to taste","estimated_cost":20}]', '["Peel and cut yam into medium cubes","If using stockfish and dried fish, soak and boil until tender","Blend tomatoes, red peppers, scotch bonnets and onions","Place yam in a pot with water to barely cover","Add palm oil and bring to a boil","Add blended pepper mixture","Add crayfish, seasoning cubes and salt","Add stockfish and dried fish if using","Cook on medium heat for 20-25 minutes","As yam softens, use a wooden spoon to mash some pieces","This creates a thick, creamy consistency","Add more water if needed for desired thickness","Adjust seasoning to taste","Serve hot, optionally with fried fish or plantain"]', 15, 30, 6, 5070, '{"calories":400,"protein":12,"carbs":65,"fats":13,"fiber":5}', '["comfort-food","traditional","filling","popular","budget-friendly"]', NULL, '2025-10-13T09:15:16.025Z', '2025-10-13T09:15:16.025Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6ec44cae56206536830f', 'Ofada Rice with Ayamase Sauce', 'Unpolished brown rice with spicy green pepper sauce. A premium Yoruba delicacy.', 'nigerian', 'lunch', '[{"item":"Ofada rice (local rice)","quantity":"4 cups","estimated_cost":2000},{"item":"Green bell peppers","quantity":"10 large","estimated_cost":1500},{"item":"Scotch bonnet peppers","quantity":"5 pieces","estimated_cost":250},{"item":"Assorted meat (beef, tripe, ponmo)","quantity":"700g","estimated_cost":3500},{"item":"Stockfish","quantity":"100g","estimated_cost":500},{"item":"Dried fish","quantity":"100g","estimated_cost":500},{"item":"Locust beans (iru)","quantity":"3 tbsp","estimated_cost":300},{"item":"Palm oil","quantity":"1.5 cups","estimated_cost":1200},{"item":"Onions","quantity":"2 large","estimated_cost":200},{"item":"Seasoning cubes","quantity":"4","estimated_cost":200},{"item":"Salt","quantity":"to taste","estimated_cost":20}]', '["Wash ofada rice multiple times to remove stones and dirt","Boil rice with excess water until tender (30-40 mins)","Drain and rinse with cold water, set aside","Season and cook assorted meat until tender","Soak and cook stockfish and dried fish","Roughly blend green peppers and scotch bonnets (not too smooth)","Bleach palm oil until it loses raw smell (5-7 minutes)","Add sliced onions and fry until dark","Add blended peppers carefully (it will splatter)","Fry on medium heat for 15-20 minutes, stirring often","Add locust beans, cooked meat, stockfish, dried fish","Add seasoning and salt","Simmer for 10 minutes","Serve ayamase sauce over ofada rice with boiled eggs"]', 30, 60, 6, 10170, '{"calories":550,"protein":28,"carbs":65,"fats":22,"fiber":5}', '["premium","yoruba-cuisine","special","spicy","traditional"]', NULL, '2025-10-13T09:15:16.025Z', '2025-10-13T09:15:16.025Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6ec44cae562065368311', 'Ji-akwukwo-nni (Yam Porridge with Vegetables)', 'Traditional Igbo yam porridge loaded with vegetables. Nutritious, colorful and delicious.', 'nigerian', 'lunch', '[{"item":"White yam","quantity":"1.5kg","estimated_cost":1800},{"item":"Stockfish","quantity":"150g","estimated_cost":800},{"item":"Dried fish","quantity":"100g","estimated_cost":500},{"item":"Ugwu (pumpkin leaves)","quantity":"2 bunches","estimated_cost":200},{"item":"Waterleaf","quantity":"1 bunch","estimated_cost":150},{"item":"Palm oil","quantity":"3/4 cup","estimated_cost":600},{"item":"Crayfish (ground)","quantity":"3 tbsp","estimated_cost":450},{"item":"Pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Onions","quantity":"1 large","estimated_cost":100},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150},{"item":"Salt","quantity":"to taste","estimated_cost":20}]', '["Peel and cut yam into medium cubes","Soak and boil stockfish and dried fish until tender","Wash and slice ugwu and waterleaf","Blend pepper and onions","Place yam in a pot with water to barely cover","Add stockfish, dried fish, palm oil","Add blended pepper, crayfish, seasoning and salt","Cook on medium heat for 20 minutes until yam is soft","Use a wooden spoon to mash some yam pieces for thickness","Add more water if too thick","Add waterleaf and cook for 5 minutes","Add ugwu leaves and stir gently","Cook for 3 more minutes","Serve hot as a complete meal"]', 20, 35, 6, 4920, '{"calories":420,"protein":18,"carbs":62,"fats":14,"fiber":6}', '["traditional","igbo-cuisine","vegetable-rich","nutritious","filling"]', NULL, '2025-10-13T09:15:16.025Z', '2025-10-13T09:15:16.025Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6ec44cae562065368310', 'Beans Porridge (Ewa Oloyin)', 'Hearty, nutritious porridge made with beans and plantain. Comfort food at its finest.', 'nigerian', 'lunch', '[{"item":"Honey beans (oloyin)","quantity":"4 cups","estimated_cost":1600},{"item":"Ripe plantains","quantity":"3 large","estimated_cost":600},{"item":"Palm oil","quantity":"3/4 cup","estimated_cost":600},{"item":"Dried fish","quantity":"150g","estimated_cost":800},{"item":"Crayfish (ground)","quantity":"3 tbsp","estimated_cost":450},{"item":"Tomatoes","quantity":"3 medium","estimated_cost":300},{"item":"Onions","quantity":"2 medium","estimated_cost":200},{"item":"Pepper","quantity":"4 pieces","estimated_cost":200},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150},{"item":"Salt","quantity":"to taste","estimated_cost":20}]', '["Wash beans and pick out stones and chaff","Boil beans with plenty of water until soft (1-2 hours or use pressure cooker)","Peel and dice plantains into chunks","Blend tomatoes, peppers and onions","When beans are soft, add more water if needed for porridge consistency","Add palm oil, blended tomatoes mixture","Add dried fish, crayfish, seasoning and salt","Add diced plantains","Cook for 15-20 minutes until plantains are soft","Stir occasionally to prevent sticking","Adjust seasoning and add water if too thick","Serve hot as a complete meal"]', 15, 90, 6, 4920, '{"calories":450,"protein":20,"carbs":68,"fats":12,"fiber":15}', '["budget-friendly","protein-rich","high-fiber","filling","comfort-food"]', NULL, '2025-10-13T09:15:16.025Z', '2025-10-13T09:15:16.025Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6ec44cae56206536830d', 'Coconut Rice', 'Fragrant rice cooked in rich coconut milk. Sweet, aromatic and perfect for special meals.', 'nigerian', 'lunch', '[{"item":"Long grain rice","quantity":"4 cups","estimated_cost":1200},{"item":"Coconut milk","quantity":"3 cups","estimated_cost":900},{"item":"Coconut flakes","quantity":"1/2 cup","estimated_cost":300},{"item":"Onions","quantity":"1 large","estimated_cost":100},{"item":"Garlic (minced)","quantity":"2 cloves","estimated_cost":50},{"item":"Ginger (grated)","quantity":"1 tsp","estimated_cost":50},{"item":"Green bell pepper","quantity":"1 medium","estimated_cost":150},{"item":"Butter or vegetable oil","quantity":"3 tbsp","estimated_cost":200},{"item":"Seasoning cubes","quantity":"2","estimated_cost":100},{"item":"Thyme","quantity":"1 tsp","estimated_cost":50},{"item":"Bay leaves","quantity":"2","estimated_cost":50},{"item":"Salt","quantity":"to taste","estimated_cost":20}]', '["Wash rice and soak for 15 minutes, then drain","Heat butter/oil in a pot","Sauté onions, garlic and ginger until fragrant","Add rice and stir for 2 minutes","Add coconut milk, water (1 cup), thyme, bay leaves","Add seasoning cubes and salt","Bring to a boil, then reduce heat to low","Cover and simmer for 20-25 minutes until rice is tender","Toast coconut flakes in a dry pan until golden","Add diced green peppers to rice in last 5 minutes","Fluff rice with a fork","Garnish with toasted coconut flakes","Serve with grilled chicken, fish or beef stew"]', 20, 30, 6, 3170, '{"calories":380,"protein":8,"carbs":62,"fats":14,"fiber":2}', '["special-occasion","aromatic","creamy","coconut-flavor","popular"]', NULL, '2025-10-13T09:15:16.024Z', '2025-10-13T09:15:16.024Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6ec44cae56206536830c', 'Nigerian Fried Rice', 'Colorful, flavorful party rice loaded with vegetables and proteins. A must-have at celebrations and special occasions.', 'nigerian', 'lunch', '[{"item":"Long grain rice","quantity":"4 cups","estimated_cost":1200},{"item":"Chicken","quantity":"500g","estimated_cost":2500},{"item":"Liver (chicken/beef)","quantity":"200g","estimated_cost":600},{"item":"Shrimp (optional)","quantity":"150g","estimated_cost":1200},{"item":"Mixed vegetables (carrots, green beans, peas, sweet corn)","quantity":"3 cups","estimated_cost":800},{"item":"Green bell pepper","quantity":"2 medium","estimated_cost":300},{"item":"Onions","quantity":"2 large","estimated_cost":200},{"item":"Vegetable oil","quantity":"1/2 cup","estimated_cost":300},{"item":"Curry powder","quantity":"2 tbsp","estimated_cost":100},{"item":"Thyme","quantity":"1 tsp","estimated_cost":50},{"item":"Seasoning cubes","quantity":"4","estimated_cost":200},{"item":"Butter","quantity":"3 tbsp","estimated_cost":300},{"item":"Salt","quantity":"to taste","estimated_cost":20}]', '["Parboil rice until 70% done, rinse with cold water and drain","Season and cook chicken until tender, dice into small pieces","Clean and dice liver, season lightly","Dice all vegetables into small cubes","Heat oil in a large pot or wok","Stir-fry onions until translucent","Add liver and fry for 3 minutes","Add shrimp if using, fry for 2 minutes","Add all vegetables except green peppers, stir-fry for 5 minutes","Add curry powder, thyme, butter and seasoning cubes","Add the parboiled rice and stir well to coat with oil and spices","Add a little chicken stock if needed (rice should not be soggy)","Add diced chicken and green peppers","Stir-fry everything together for 5-7 minutes","Taste and adjust seasoning","Serve hot with salad, coleslaw and fried plantain"]', 25, 35, 8, 7770, '{"calories":420,"protein":22,"carbs":58,"fats":12,"fiber":4}', '["party-favorite","colorful","protein-rich","special-occasion","popular"]', NULL, '2025-10-13T09:15:16.024Z', '2025-10-13T09:15:16.024Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec656a9c8f735097245aad', 'Yam and Garden Egg Sauce', 'Boiled yam with spicy garden egg sauce. A nutritious twist on the classic yam breakfast.', 'nigerian', 'breakfast', '[{"item":"White yam","quantity":"1kg","estimated_cost":1200},{"item":"Garden eggs","quantity":"10 pieces","estimated_cost":300},{"item":"Eggs (optional)","quantity":"3 large","estimated_cost":450},{"item":"Tomatoes","quantity":"3 medium","estimated_cost":300},{"item":"Onions","quantity":"1 large","estimated_cost":100},{"item":"Pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Palm oil","quantity":"1/4 cup","estimated_cost":300},{"item":"Crayfish (ground)","quantity":"2 tbsp","estimated_cost":300},{"item":"Seasoning cube","quantity":"1","estimated_cost":50},{"item":"Salt","quantity":"to taste","estimated_cost":20}]', '["Peel, cut and boil yam until tender","Boil garden eggs until soft, then mash","Roughly blend tomatoes, onions and peppers","Heat palm oil in a pan","Fry blended mixture for 5 minutes","Add mashed garden eggs and stir well","Add crayfish, seasoning and salt","Cook for 10 minutes on medium heat","If using eggs, crack them into the sauce and scramble","Cook until eggs are done","Serve hot garden egg sauce over boiled yam"]', 15, 35, 4, 3170, '{"calories":390,"protein":12,"carbs":60,"fats":12,"fiber":6}', '["breakfast","traditional","vegetable-rich","nutritious","unique"]', NULL, '2025-10-13T08:35:22.022Z', '2025-10-13T08:35:22.022Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec656a9c8f735097245aab', 'Yam and Egg Sauce', 'Classic Nigerian breakfast of boiled yam with scrambled egg sauce. Simple, filling and nutritious.', 'nigerian', 'breakfast', '[{"item":"White yam","quantity":"1kg (medium tuber)","estimated_cost":1200},{"item":"Eggs","quantity":"6 large","estimated_cost":900},{"item":"Tomatoes","quantity":"4 medium","estimated_cost":400},{"item":"Onions","quantity":"1 large","estimated_cost":100},{"item":"Red bell pepper","quantity":"1 medium","estimated_cost":150},{"item":"Scotch bonnet pepper","quantity":"1-2 pieces","estimated_cost":100},{"item":"Vegetable oil","quantity":"1/4 cup","estimated_cost":200},{"item":"Seasoning cubes","quantity":"2","estimated_cost":100},{"item":"Salt","quantity":"to taste","estimated_cost":20},{"item":"Curry powder (optional)","quantity":"1 tsp","estimated_cost":50}]', '["Peel yam and cut into medium-sized chunks","Wash yam pieces thoroughly","Place yam in a pot with water and salt","Boil until tender but firm (about 20-25 minutes)","Drain and set aside, keep warm","Dice tomatoes, onions and peppers","Break eggs into a bowl and whisk lightly","Heat oil in a frying pan","Sauté onions until soft","Add tomatoes and peppers, fry for 5-7 minutes","Add seasoning cubes, curry powder if using, and salt","Pour in whisked eggs","Scramble gently until eggs are cooked but still moist","Serve egg sauce over boiled yam while hot"]', 10, 30, 4, 3220, '{"calories":420,"protein":18,"carbs":58,"fats":14,"fiber":4}', '["breakfast","traditional","protein-rich","filling","popular"]', NULL, '2025-10-13T08:35:22.022Z', '2025-10-13T08:35:22.022Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec656a9c8f735097245aaa', 'Nigerian Pancakes', 'Fluffy, slightly sweet pancakes that are perfect for breakfast. A Nigerian favorite that''s easy to make and loved by all.', 'nigerian', 'breakfast', '[{"item":"All-purpose flour","quantity":"3 cups","estimated_cost":600},{"item":"Sugar","quantity":"1/2 cup","estimated_cost":200},{"item":"Eggs","quantity":"3 large","estimated_cost":450},{"item":"Milk (liquid or evaporated)","quantity":"2 cups","estimated_cost":600},{"item":"Butter (melted)","quantity":"4 tbsp","estimated_cost":400},{"item":"Baking powder","quantity":"2 tsp","estimated_cost":100},{"item":"Vanilla extract","quantity":"1 tsp","estimated_cost":150},{"item":"Salt","quantity":"1/4 tsp","estimated_cost":20},{"item":"Nutmeg (ground)","quantity":"1/4 tsp","estimated_cost":50},{"item":"Vegetable oil for frying","quantity":"as needed","estimated_cost":300}]', '["In a large bowl, mix flour, sugar, baking powder, salt and nutmeg","In another bowl, whisk eggs, milk, melted butter and vanilla extract","Pour wet ingredients into dry ingredients","Mix until smooth but don''t overmix (some lumps are okay)","Let batter rest for 10 minutes","Heat a non-stick frying pan or griddle over medium heat","Lightly grease with oil or butter","Pour about 1/4 cup of batter for each pancake","Cook until bubbles form on surface (about 2-3 minutes)","Flip and cook other side until golden brown (1-2 minutes)","Repeat with remaining batter","Serve warm with honey, jam, or chocolate spread"]', 15, 25, 6, 2870, '{"calories":340,"protein":10,"carbs":52,"fats":11,"fiber":2}', '["breakfast","quick","family-favorite","kids-friendly","sweet"]', NULL, '2025-10-13T08:35:22.022Z', '2025-10-13T08:35:22.022Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec656a9c8f735097245aac', 'Yam and Fried Egg', 'Boiled yam served with perfectly fried eggs. Simple, satisfying breakfast that''s ready in minutes.', 'nigerian', 'breakfast', '[{"item":"White yam","quantity":"1kg (medium tuber)","estimated_cost":1200},{"item":"Eggs","quantity":"6 large","estimated_cost":900},{"item":"Vegetable oil","quantity":"1/4 cup","estimated_cost":200},{"item":"Salt","quantity":"to taste","estimated_cost":20},{"item":"Black pepper","quantity":"to taste","estimated_cost":50},{"item":"Onions (sliced, optional)","quantity":"1 small","estimated_cost":50}]', '["Peel and cut yam into chunks","Wash yam thoroughly","Boil yam in salted water until tender (20-25 minutes)","Drain and keep warm","Heat oil in a frying pan","Crack eggs into the pan (or fry one at a time)","Season with salt and black pepper","Fry until whites are set and yolk reaches desired doneness","Can fry sunny side up or flip for over-easy/hard","Optional: sauté sliced onions separately and serve alongside","Serve fried eggs immediately over hot yam"]', 5, 25, 4, 2420, '{"calories":400,"protein":16,"carbs":56,"fats":13,"fiber":4}', '["breakfast","quick","simple","traditional","budget-friendly"]', NULL, '2025-10-13T08:35:22.022Z', '2025-10-13T08:35:22.022Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6308c913c7f0e0ffb9b0', 'Gbegiri Soup', 'Smooth Yoruba bean soup, traditionally served with ewedu and amala. Creamy and filling.', 'nigerian', 'dinner', '[{"item":"Beans (brown or white)","quantity":"3 cups","estimated_cost":900},{"item":"Palm oil","quantity":"1/2 cup","estimated_cost":400},{"item":"Pepper (grounded)","quantity":"2 tbsp","estimated_cost":100},{"item":"Onions","quantity":"1 medium","estimated_cost":100},{"item":"Seasoning cubes","quantity":"2","estimated_cost":100}]', '["Wash beans and remove chaff","Boil beans with enough water until very soft (pressure cooker speeds this up)","Blend cooked beans with some cooking water until very smooth","Pour blended beans back into pot","Add palm oil and stir well","Add pepper, chopped onions and seasoning","Add more water if too thick, stir continuously","Cook on low heat for 10-15 minutes, stirring frequently to prevent burning","Serve hot with ewedu, stew and amala"]', 10, 90, 6, 1600, '{"calories":280,"protein":16,"carbs":38,"fats":8,"fiber":12}', '["yoruba-cuisine","budget-friendly","vegetarian","protein-rich","traditional"]', NULL, '2025-10-13T08:25:12.375Z', '2025-10-13T08:25:12.375Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6308c913c7f0e0ffb9af', 'Oha Soup (Ora Soup)', 'Prized Igbo soup made with rare oha leaves. Considered a delicacy and highly nutritious.', 'nigerian', 'dinner', '[{"item":"Oha leaves (fresh)","quantity":"2 cups shredded","estimated_cost":1500},{"item":"Cocoyam","quantity":"4 medium","estimated_cost":400},{"item":"Stockfish","quantity":"200g","estimated_cost":1000},{"item":"Dried fish","quantity":"150g","estimated_cost":800},{"item":"Beef or goat meat","quantity":"500g","estimated_cost":3000},{"item":"Uziza leaves","quantity":"small bunch","estimated_cost":100},{"item":"Palm oil","quantity":"3/4 cup","estimated_cost":600},{"item":"Crayfish","quantity":"4 tbsp","estimated_cost":600},{"item":"Pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150}]', '["Peel and boil cocoyam until soft, then pound smooth","Season and cook meat until tender","Boil stockfish and dried fish","Heat palm oil in a pot","Add meat stock and bring to a boil","Add pounded cocoyam while stirring to thicken","Add meat, stockfish, dried fish, pepper, crayfish","Season and simmer for 15 minutes","Add shredded oha leaves (do not stir vigorously)","Add uziza leaves","Allow to simmer for 3 minutes without much stirring","Serve immediately with pounded yam or eba"]', 20, 40, 5, 8300, '{"calories":480,"protein":30,"carbs":24,"fats":30,"fiber":5}', '["premium","traditional","igbo-cuisine","rare","special"]', NULL, '2025-10-13T08:25:12.375Z', '2025-10-13T08:25:12.375Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6308c913c7f0e0ffb9b6', 'Miyan Gyada (Groundnut Soup)', 'Rich Northern Nigerian peanut soup. Creamy, protein-packed and absolutely delicious.', 'nigerian', 'dinner', '[{"item":"Groundnut paste","quantity":"2 cups","estimated_cost":1200},{"item":"Beef","quantity":"500g","estimated_cost":3000},{"item":"Dried fish","quantity":"100g","estimated_cost":500},{"item":"Spinach","quantity":"2 bunches","estimated_cost":300},{"item":"Palm oil","quantity":"1/2 cup","estimated_cost":400},{"item":"Tomatoes","quantity":"4 medium","estimated_cost":400},{"item":"Onions","quantity":"2 medium","estimated_cost":200},{"item":"Pepper","quantity":"4 pieces","estimated_cost":200},{"item":"Locust beans (dadawa)","quantity":"2 tbsp","estimated_cost":200},{"item":"Seasoning cubes","quantity":"2","estimated_cost":100}]', '["Season and cook beef until very tender","Blend tomatoes, pepper and onions","Heat palm oil and fry tomato mixture until oil floats","Add meat stock and bring to boil","Mix groundnut paste with water to form smooth paste","Add groundnut paste to soup while stirring","Add beef, dried fish, locust beans and seasoning","Simmer on low heat for 20-25 minutes, stirring occasionally","Add more water if too thick","Wash and add spinach","Cook for 5 minutes and serve with tuwo or rice"]', 15, 50, 6, 6500, '{"calories":520,"protein":32,"carbs":24,"fats":36,"fiber":5}', '["northern-cuisine","hausa","protein-rich","creamy","popular"]', NULL, '2025-10-13T08:25:12.375Z', '2025-10-13T08:25:12.375Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6308c913c7f0e0ffb9ad', 'Afia Efere (Efik White Soup)', 'Calabar''s signature white soup without palm oil. Creamy, aromatic and packed with seafood and meat.', 'nigerian', 'dinner', '[{"item":"Yam (for thickening)","quantity":"1 medium","estimated_cost":800},{"item":"Catfish (fresh)","quantity":"2 large","estimated_cost":2500},{"item":"Stockfish","quantity":"200g","estimated_cost":1000},{"item":"Periwinkle","quantity":"1 cup","estimated_cost":600},{"item":"Goat meat","quantity":"500g","estimated_cost":3500},{"item":"Ehuru (calabash nutmeg)","quantity":"3 pieces","estimated_cost":150},{"item":"Uziza seeds (ground)","quantity":"2 tbsp","estimated_cost":300},{"item":"Crayfish","quantity":"4 tbsp","estimated_cost":600},{"item":"Pepper","quantity":"4 pieces","estimated_cost":200},{"item":"Onions","quantity":"1 medium","estimated_cost":100},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150}]', '["Peel, cut and boil yam until very soft","Pound or blend yam into smooth paste","Season and cook goat meat until tender","Boil stockfish separately until soft","Clean and cut catfish into chunks","Roast and grind ehuru (calabash nutmeg)","In a pot, add meat stock and bring to a boil","Add pounded yam gradually while stirring to dissolve","Add goat meat, stockfish, catfish, periwinkle","Add ground ehuru, uziza seeds, crayfish, pepper and seasoning","Simmer for 15-20 minutes on medium heat","Adjust seasoning and serve with fufu or eba"]', 25, 45, 6, 9900, '{"calories":490,"protein":38,"carbs":25,"fats":26,"fiber":3}', '["traditional","calabar-cuisine","premium","seafood"]', NULL, '2025-10-13T08:25:12.375Z', '2025-10-13T08:25:12.375Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6308c913c7f0e0ffb9b4', 'Miyan Kuka (Baobab Soup)', 'Traditional Hausa soup made from powdered baobab leaves. Light, nutritious and distinctively Northern.', 'nigerian', 'dinner', '[{"item":"Kuka powder (baobab)","quantity":"1 cup","estimated_cost":800},{"item":"Beef","quantity":"500g","estimated_cost":3000},{"item":"Dried fish (optional)","quantity":"100g","estimated_cost":500},{"item":"Palm oil or vegetable oil","quantity":"1/2 cup","estimated_cost":300},{"item":"Tomatoes","quantity":"3 medium","estimated_cost":300},{"item":"Onions","quantity":"2 medium","estimated_cost":200},{"item":"Pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Locust beans (dadawa)","quantity":"2 tbsp","estimated_cost":200},{"item":"Seasoning cubes","quantity":"2","estimated_cost":100}]', '["Season and cook beef until tender, reserve stock","Blend tomatoes, pepper and onions","Heat oil in a pot and fry blended mixture","Add meat stock and bring to boil","Add cooked beef, dried fish if using","Add locust beans and seasoning","In a bowl, mix kuka powder with cold water to form paste","Add kuka paste to the soup gradually while stirring","Simmer for 10-15 minutes","Adjust seasoning and serve with tuwo shinkafa or tuwo masara"]', 15, 40, 5, 5550, '{"calories":380,"protein":28,"carbs":22,"fats":20,"fiber":8}', '["northern-cuisine","hausa","traditional","high-fiber","nutritious"]', NULL, '2025-10-13T08:25:12.375Z', '2025-10-13T08:25:12.375Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6308c913c7f0e0ffb9b1', 'Atama Soup', 'Delicious Calabar soup made with atama leaves. Rich, flavorful and highly nutritious.', 'nigerian', 'dinner', '[{"item":"Atama leaves (shredded)","quantity":"2 cups","estimated_cost":800},{"item":"Palm kernel (beletete)","quantity":"2 cups","estimated_cost":1200},{"item":"Stockfish","quantity":"200g","estimated_cost":1000},{"item":"Dried fish","quantity":"150g","estimated_cost":800},{"item":"Periwinkle","quantity":"1 cup","estimated_cost":600},{"item":"Beef or goat meat","quantity":"500g","estimated_cost":3000},{"item":"Waterleaf","quantity":"1 bunch","estimated_cost":150},{"item":"Crayfish","quantity":"4 tbsp","estimated_cost":600},{"item":"Pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Onions","quantity":"1 medium","estimated_cost":100},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150}]', '["Extract palm kernel cream (beletete) or use ready-made","Season and cook meat until tender","Boil stockfish and dried fish separately","Pour palm kernel into pot and bring to a boil","Add meat stock, pepper, onions and seasoning","Add meat, stockfish, dried fish, periwinkle","Add crayfish and simmer for 20 minutes","Add waterleaf and cook for 5 minutes","Add shredded atama leaves","Stir gently and cook for 5 more minutes","Serve with eba, fufu or garri"]', 20, 45, 6, 8550, '{"calories":510,"protein":28,"carbs":15,"fats":40,"fiber":6}', '["traditional","calabar-cuisine","premium","rich-flavor"]', NULL, '2025-10-13T08:25:12.375Z', '2025-10-13T08:25:12.375Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6308c913c7f0e0ffb9b2', 'Fisherman Soup', 'Native soup loaded with fresh fish and seafood. A true coastal delicacy from Rivers State.', 'nigerian', 'dinner', '[{"item":"Fresh catfish","quantity":"2 large","estimated_cost":2500},{"item":"Fresh tilapia","quantity":"2 medium","estimated_cost":1500},{"item":"Periwinkle","quantity":"2 cups","estimated_cost":1200},{"item":"Crab","quantity":"2 medium","estimated_cost":1500},{"item":"Yam (for thickening)","quantity":"1 small","estimated_cost":500},{"item":"Uziza leaves","quantity":"1 bunch","estimated_cost":200},{"item":"Scent leaves","quantity":"small bunch","estimated_cost":150},{"item":"Palm oil","quantity":"1/2 cup","estimated_cost":400},{"item":"Crayfish","quantity":"5 tbsp","estimated_cost":750},{"item":"Pepper","quantity":"5 pieces","estimated_cost":250},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150}]', '["Clean all fish and seafood thoroughly","Cut catfish and tilapia into chunks","Peel and boil yam, then pound smooth","In a pot, add water, palm oil and bring to boil","Add pepper, crayfish and seasoning","Add catfish and tilapia, cook for 10 minutes","Add periwinkle and crab","Add pounded yam to thicken soup","Simmer for 15 minutes","Add uziza and scent leaves","Cook for 3 more minutes","Serve hot with eba or pounded yam"]', 25, 35, 5, 9100, '{"calories":450,"protein":42,"carbs":18,"fats":24,"fiber":3}', '["seafood","premium","rivers-cuisine","protein-rich","special"]', NULL, '2025-10-13T08:25:12.375Z', '2025-10-13T08:25:12.375Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6308c913c7f0e0ffb9b5', 'Miyan Taushe (Pumpkin Soup)', 'Creamy Northern Nigerian pumpkin soup. Sweet, nutritious and beloved across the North.', 'nigerian', 'dinner', '[{"item":"Pumpkin (diced)","quantity":"1kg","estimated_cost":800},{"item":"Beef","quantity":"500g","estimated_cost":3000},{"item":"Dried fish","quantity":"100g","estimated_cost":500},{"item":"Spinach or alayyahu","quantity":"2 bunches","estimated_cost":300},{"item":"Palm oil","quantity":"1/2 cup","estimated_cost":400},{"item":"Tomatoes","quantity":"3 medium","estimated_cost":300},{"item":"Onions","quantity":"2 medium","estimated_cost":200},{"item":"Pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Locust beans (dadawa)","quantity":"2 tbsp","estimated_cost":200},{"item":"Seasoning cubes","quantity":"2","estimated_cost":100}]', '["Peel and dice pumpkin into small cubes","Season and cook beef until tender","Boil pumpkin until very soft, then mash or blend","Blend tomatoes, pepper and onions","Heat palm oil and fry tomato mixture","Add meat stock and mashed pumpkin","Add beef, dried fish, locust beans","Add seasoning and simmer for 15 minutes","Wash and add spinach/alayyahu","Cook for 5 more minutes","Serve with tuwo shinkafa, tuwo masara or rice"]', 20, 45, 6, 5950, '{"calories":360,"protein":26,"carbs":28,"fats":18,"fiber":6}', '["northern-cuisine","hausa","vegetable-rich","creamy","nutritious"]', NULL, '2025-10-13T08:25:12.375Z', '2025-10-13T08:25:12.375Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6308c913c7f0e0ffb9ae', 'Ofe Owerri', 'Special Igbo soup from Owerri with multiple vegetables and generous protein. A true delicacy.', 'nigerian', 'dinner', '[{"item":"Cocoyam","quantity":"5 medium","estimated_cost":500},{"item":"Stockfish","quantity":"200g","estimated_cost":1000},{"item":"Dried fish","quantity":"150g","estimated_cost":800},{"item":"Beef","quantity":"500g","estimated_cost":3000},{"item":"Uziza leaves","quantity":"1 bunch","estimated_cost":200},{"item":"Ugu leaves","quantity":"1 bunch","estimated_cost":100},{"item":"Palm oil","quantity":"3/4 cup","estimated_cost":600},{"item":"Crayfish","quantity":"4 tbsp","estimated_cost":600},{"item":"Pepper","quantity":"4 pieces","estimated_cost":200},{"item":"Onions","quantity":"1 medium","estimated_cost":100},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150}]', '["Peel and boil cocoyam until very soft","Pound cocoyam into smooth paste","Season and cook beef until tender","Boil stockfish and dried fish separately","Wash and slice uziza and ugu leaves","Heat palm oil in a large pot","Add meat stock and bring to a boil","Add pounded cocoyam gradually while stirring","Add beef, stockfish, dried fish, crayfish","Add pepper, onions and seasoning","Simmer for 20 minutes","Add vegetables and cook for 3-5 minutes","Serve with pounded yam or eba"]', 30, 50, 6, 7250, '{"calories":470,"protein":32,"carbs":28,"fats":28,"fiber":6}', '["traditional","igbo-cuisine","special-occasion","vegetable-rich"]', NULL, '2025-10-13T08:25:12.375Z', '2025-10-13T08:25:12.375Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ec6308c913c7f0e0ffb9b3', 'Owho Soup', 'Traditional Delta soup made with fresh okra and ora seeds. Uniquely delicious and nutritious.', 'nigerian', 'dinner', '[{"item":"Fresh okra","quantity":"500g","estimated_cost":400},{"item":"Ora seeds (ground)","quantity":"1 cup","estimated_cost":1000},{"item":"Stockfish","quantity":"200g","estimated_cost":1000},{"item":"Dried fish","quantity":"150g","estimated_cost":800},{"item":"Beef","quantity":"500g","estimated_cost":3000},{"item":"Palm oil","quantity":"3/4 cup","estimated_cost":600},{"item":"Waterleaf","quantity":"1 bunch","estimated_cost":150},{"item":"Crayfish","quantity":"4 tbsp","estimated_cost":600},{"item":"Pepper","quantity":"4 pieces","estimated_cost":200},{"item":"Onions","quantity":"1 medium","estimated_cost":100},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150}]', '["Wash and grate okra","Season and cook beef until tender","Boil stockfish and dried fish","Heat palm oil in a pot","Add meat stock and bring to a boil","Add ground ora seeds and stir well","Add beef, stockfish, dried fish","Add pepper, crayfish, onions and seasoning","Simmer for 15 minutes","Add grated okra and stir","Add waterleaf and cook for 5 minutes","Serve with starch, eba or garri"]', 20, 40, 6, 8000, '{"calories":460,"protein":30,"carbs":20,"fats":30,"fiber":7}', '["traditional","delta-cuisine","unique","nutritious"]', NULL, '2025-10-13T08:25:12.375Z', '2025-10-13T08:25:12.375Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ebd0c25cce1f08e6f5149a', 'Ofe Nsala (White Soup)', 'Igbo delicacy also known as white soup. Made without palm oil, using yam as a thickener.', 'nigerian', 'dinner', '[{"item":"White yam","quantity":"1 medium","estimated_cost":800},{"item":"Stockfish","quantity":"200g","estimated_cost":1000},{"item":"Catfish (fresh)","quantity":"2 medium","estimated_cost":2500},{"item":"Chicken","quantity":"500g","estimated_cost":2500},{"item":"Uziza seeds (ground)","quantity":"2 tbsp","estimated_cost":300},{"item":"Crayfish","quantity":"3 tbsp","estimated_cost":450},{"item":"Pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Onions","quantity":"1 medium","estimated_cost":100},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150},{"item":"Utazi leaves (optional)","quantity":"small bunch","estimated_cost":100}]', '["Peel, cut and boil yam until soft, then pound or blend smoothly","Season and cook chicken until tender","Boil stockfish separately","Clean and cut catfish into pieces","In a pot, add chicken stock and bring to a boil","Add pounded yam gradually while stirring to dissolve","Add chicken, stockfish, catfish, pepper and seasoning","Add ground uziza seeds and crayfish","Simmer for 15 minutes","Add utazi leaves if using and cook for 2 minutes","Serve hot with pounded yam or fufu"]', 25, 40, 5, 7950, '{"calories":460,"protein":35,"carbs":28,"fats":22,"fiber":4}', '["traditional","igbo-cuisine","special-occasion","unique"]', NULL, '2025-10-12T22:01:06.633Z', '2025-10-12T22:01:06.633Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ebd0c25cce1f08e6f5149c', 'Ewedu Soup', 'Yoruba jute leaf soup, smooth and slimy. Perfect with amala and gbegiri.', 'nigerian', 'dinner', '[{"item":"Ewedu leaves (jute leaves)","quantity":"4 bunches","estimated_cost":400},{"item":"Locust beans (iru)","quantity":"2 tbsp","estimated_cost":200},{"item":"Pepper","quantity":"2 pieces","estimated_cost":100},{"item":"Seasoning cube","quantity":"1","estimated_cost":50},{"item":"Salt","quantity":"to taste","estimated_cost":20}]', '["Wash ewedu leaves thoroughly","Boil water in a pot","Add ewedu leaves to boiling water","Cook for 3-5 minutes until leaves are soft","Blend using a traditional broom (ijabe) or hand blender until smooth","Add locust beans, pepper and seasoning","Stir well and simmer for 2 minutes","Serve hot with amala, gbegiri and assorted meats"]', 10, 10, 4, 770, '{"calories":45,"protein":3,"carbs":8,"fats":1,"fiber":4}', '["quick","yoruba-cuisine","budget-friendly","light","traditional"]', NULL, '2025-10-12T22:01:06.633Z', '2025-10-12T22:01:06.633Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ebd0c25cce1f08e6f51496', 'Okra Soup (Ila Alasepo)', 'Fresh okra soup with assorted meats and seafood. Light, nutritious and perfect for the whole family.', 'nigerian', 'dinner', '[{"item":"Fresh okra","quantity":"500g","estimated_cost":400},{"item":"Stockfish","quantity":"150g","estimated_cost":800},{"item":"Dried fish","quantity":"100g","estimated_cost":500},{"item":"Palm oil","quantity":"1/2 cup","estimated_cost":400},{"item":"Beef or goat meat","quantity":"400g","estimated_cost":2400},{"item":"Crayfish","quantity":"2 tbsp","estimated_cost":300},{"item":"Pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Onions","quantity":"1 medium","estimated_cost":100},{"item":"Seasoning cubes","quantity":"2","estimated_cost":100}]', '["Wash and grate or blend okra to desired consistency","Season and cook meat until tender","Soak and cook stockfish and dried fish","In a pot, heat palm oil and fry chopped onions","Add meat stock, pepper, crayfish and seasoning","Bring to a boil and add okra","Add cooked meats and fish","Stir gently and cook for 7-10 minutes","Serve hot with eba, amala or rice"]', 15, 30, 5, 5150, '{"calories":350,"protein":26,"carbs":14,"fats":22,"fiber":5}', '["quick","nutritious","family-friendly","budget-friendly"]', NULL, '2025-10-12T22:01:06.633Z', '2025-10-12T22:01:06.633Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ebd0c25cce1f08e6f51494', 'Egusi Soup', 'Rich melon seed soup, a staple in Nigerian cuisine. Creamy, nutritious and pairs perfectly with any swallow.', 'nigerian', 'dinner', '[{"item":"Egusi (ground melon seeds)","quantity":"2 cups","estimated_cost":1600},{"item":"Stockfish","quantity":"200g","estimated_cost":1000},{"item":"Dried fish","quantity":"150g","estimated_cost":800},{"item":"Palm oil","quantity":"3/4 cup","estimated_cost":600},{"item":"Ugwu (pumpkin leaves)","quantity":"2 bunches","estimated_cost":200},{"item":"Crayfish (ground)","quantity":"3 tbsp","estimated_cost":450},{"item":"Pepper (scotch bonnet)","quantity":"4 pieces","estimated_cost":200},{"item":"Onions","quantity":"2 medium","estimated_cost":200},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150}]', '["Soak and boil stockfish and dried fish until tender","Blend egusi with peppers and onions (or use ground egusi separately)","Heat palm oil in a pot and fry the egusi paste for 5 minutes, stirring constantly","Add fish stock gradually, stirring to avoid lumps","Add stockfish, dried fish, crayfish and seasoning","Simmer for 20 minutes on low heat, stirring occasionally","Add washed and chopped ugwu leaves","Stir well and cook for 3-5 minutes until leaves wilt","Adjust seasoning and serve hot with eba, fufu, or pounded yam"]', 20, 35, 6, 5200, '{"calories":420,"protein":24,"carbs":12,"fats":32,"fiber":6}', '["traditional","protein-rich","family-favorite","nutritious"]', NULL, '2025-10-12T22:01:06.633Z', '2025-10-12T22:01:06.633Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ebd0c25cce1f08e6f51498', 'Afang Soup', 'Traditional Cross River delicacy made with Afang leaves and waterleaf. Rich, hearty and incredibly delicious.', 'nigerian', 'dinner', '[{"item":"Afang leaves (shredded)","quantity":"3 cups","estimated_cost":1200},{"item":"Waterleaf","quantity":"2 bunches","estimated_cost":300},{"item":"Stockfish","quantity":"200g","estimated_cost":1000},{"item":"Dried fish","quantity":"150g","estimated_cost":800},{"item":"Beef","quantity":"500g","estimated_cost":3000},{"item":"Periwinkle","quantity":"1 cup","estimated_cost":600},{"item":"Palm oil","quantity":"1 cup","estimated_cost":800},{"item":"Crayfish","quantity":"4 tbsp","estimated_cost":600},{"item":"Pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150}]', '["Wash waterleaf and afang leaves thoroughly","Cook beef, stockfish and dried fish separately","Heat palm oil in a large pot","Add meat stock, pepper and seasoning","Add all cooked meats, fish and periwinkle","Bring to a boil and add waterleaf","Stir and cook for 5 minutes","Add shredded afang leaves and crayfish","Stir well and cook for 10 more minutes","Serve hot with eba, fufu or garri"]', 20, 40, 6, 8600, '{"calories":500,"protein":30,"carbs":12,"fats":38,"fiber":8}', '["traditional","premium","protein-rich","special"]', NULL, '2025-10-12T22:01:06.633Z', '2025-10-12T22:01:06.633Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ebd0c25cce1f08e6f5149b', 'Ofe Onugbu (Bitter Leaf Soup)', 'Traditional Igbo soup made with bitter leaves. Medicinal properties and rich taste.', 'nigerian', 'dinner', '[{"item":"Bitter leaf (washed)","quantity":"3 cups","estimated_cost":400},{"item":"Stockfish","quantity":"200g","estimated_cost":1000},{"item":"Dried fish","quantity":"150g","estimated_cost":800},{"item":"Beef","quantity":"500g","estimated_cost":3000},{"item":"Palm oil","quantity":"3/4 cup","estimated_cost":600},{"item":"Cocoyam (for thickening)","quantity":"3 medium","estimated_cost":300},{"item":"Crayfish","quantity":"3 tbsp","estimated_cost":450},{"item":"Pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Onions","quantity":"1 medium","estimated_cost":100},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150}]', '["Wash bitter leaves multiple times to reduce bitterness","Boil and pound cocoyam until smooth","Season and cook beef until tender","Boil stockfish and dried fish separately","Heat palm oil in a pot","Add meat stock, pepper, onions and seasoning","Add pounded cocoyam gradually while stirring","Add cooked meats, fish and crayfish","Simmer for 15 minutes","Add bitter leaves and cook for 5 minutes","Serve with pounded yam, eba or fufu"]', 30, 40, 6, 6950, '{"calories":440,"protein":30,"carbs":20,"fats":28,"fiber":7}', '["traditional","medicinal","igbo-cuisine","healthy"]', NULL, '2025-10-12T22:01:06.633Z', '2025-10-12T22:01:06.633Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ebd0c25cce1f08e6f51497', 'Edikang Ikong Soup', 'Royal vegetable soup from Calabar. Extremely nutritious with a blend of water leaves and ugwu.', 'nigerian', 'dinner', '[{"item":"Waterleaf","quantity":"3 bunches","estimated_cost":450},{"item":"Ugwu (pumpkin leaves)","quantity":"2 bunches","estimated_cost":200},{"item":"Stockfish","quantity":"200g","estimated_cost":1000},{"item":"Dried fish","quantity":"150g","estimated_cost":800},{"item":"Beef","quantity":"500g","estimated_cost":3000},{"item":"Periwinkle","quantity":"1 cup","estimated_cost":600},{"item":"Palm oil","quantity":"1 cup","estimated_cost":800},{"item":"Crayfish","quantity":"4 tbsp","estimated_cost":600},{"item":"Pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150}]', '["Wash and slice waterleaf and ugwu finely","Season and cook beef until tender","Boil stockfish and dried fish separately","Heat palm oil in a large pot","Add meat stock, pepper, crayfish and seasoning","Add cooked meats, fish and periwinkle","Bring to a boil and add waterleaf first","Cook for 5 minutes, then add ugwu","Stir well and cook for another 3 minutes","Serve immediately with eba or pounded yam"]', 25, 35, 6, 7750, '{"calories":480,"protein":32,"carbs":15,"fats":35,"fiber":7}', '["premium","nutritious","vegetable-rich","special-occasion"]', NULL, '2025-10-12T22:01:06.633Z', '2025-10-12T22:01:06.633Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ebd0c25cce1f08e6f51495', 'Ogbono Soup (Draw Soup)', 'Delicious draw soup made from ground African mango seeds. Known for its unique slimy texture and rich flavor.', 'nigerian', 'dinner', '[{"item":"Ogbono (ground)","quantity":"1.5 cups","estimated_cost":1800},{"item":"Stockfish","quantity":"200g","estimated_cost":1000},{"item":"Beef","quantity":"500g","estimated_cost":3000},{"item":"Palm oil","quantity":"1/2 cup","estimated_cost":400},{"item":"Bitter leaf or ugwu","quantity":"2 bunches","estimated_cost":300},{"item":"Crayfish","quantity":"3 tbsp","estimated_cost":450},{"item":"Pepper","quantity":"3 pieces","estimated_cost":150},{"item":"Onions","quantity":"1 medium","estimated_cost":100},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150}]', '["Season and cook beef until tender, reserve stock","Boil stockfish separately until soft","Heat palm oil in a pot until hot","Add ground ogbono and stir quickly to prevent burning","Gradually add hot stock while stirring continuously to achieve smooth consistency","Add cooked meat, stockfish, crayfish, pepper and seasoning","Simmer for 15 minutes on low heat","Add washed vegetables and stir gently","Cook for 5 more minutes and serve with eba or fufu"]', 15, 45, 6, 7350, '{"calories":480,"protein":28,"carbs":10,"fats":38,"fiber":8}', '["traditional","high-protein","popular","filling"]', NULL, '2025-10-12T22:01:06.633Z', '2025-10-12T22:01:06.633Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68ebd0c25cce1f08e6f51499', 'Banga Soup (Ofe Akwu)', 'Palm nut soup from the Niger Delta region. Rich, aromatic and absolutely delicious.', 'nigerian', 'dinner', '[{"item":"Palm nut extract (Banga)","quantity":"4 cups","estimated_cost":1500},{"item":"Stockfish","quantity":"200g","estimated_cost":1000},{"item":"Dried fish","quantity":"150g","estimated_cost":800},{"item":"Beef","quantity":"500g","estimated_cost":3000},{"item":"Banga spice","quantity":"2 tbsp","estimated_cost":300},{"item":"Bitterleaf (optional)","quantity":"1 bunch","estimated_cost":150},{"item":"Crayfish","quantity":"3 tbsp","estimated_cost":450},{"item":"Pepper","quantity":"4 pieces","estimated_cost":200},{"item":"Onions","quantity":"1 medium","estimated_cost":100},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150}]', '["Extract palm nut cream or use ready-made banga","Season and cook beef until tender","Boil stockfish and dried fish","Pour banga extract into a pot and bring to a boil","Add meat stock, banga spice, pepper and seasoning","Add cooked meats and fish","Add crayfish and onions","Simmer for 20-25 minutes","Add washed bitterleaf if using","Cook for 5 more minutes and serve with starch or eba"]', 20, 45, 6, 7650, '{"calories":520,"protein":28,"carbs":18,"fats":40,"fiber":6}', '["traditional","rich-flavor","popular","delta-cuisine"]', NULL, '2025-10-12T22:01:06.633Z', '2025-10-12T22:01:06.633Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68db64cf5684782e3d7f64b9', 'Vegetable Soup (Efo Riro)', 'Rich spinach soup packed with nutrients', 'nigerian', 'dinner', '[{"item":"Spinach","quantity":"3 bunches","estimated_cost":300},{"item":"Stockfish","quantity":"150g","estimated_cost":800},{"item":"Palm oil","quantity":"1/2 cup","estimated_cost":400},{"item":"Tomatoes","quantity":"4 medium","estimated_cost":300},{"item":"Crayfish","quantity":"2 tbsp","estimated_cost":300},{"item":"Peppers","quantity":"3","estimated_cost":150}]', '["Wash and chop vegetables","Blend tomatoes and peppers","Cook stockfish until tender","Fry tomato mixture in palm oil","Add vegetables and cook briefly","Season and serve with eba or pounded yam"]', 15, 30, 5, 2250, '{"calories":280,"protein":18,"carbs":15,"fats":18,"fiber":6}', '["healthy","vegetable-rich","quick"]', NULL, '2025-09-30T11:04:15.077Z', '2025-09-30T11:04:15.077Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68db64cf5684782e3d7f64b5', 'Jollof Rice with Chicken', 'Classic Nigerian party rice with tender chicken pieces', 'nigerian', 'lunch', '[{"item":"Rice","quantity":"4 cups","estimated_cost":1200},{"item":"Tomatoes","quantity":"6 large","estimated_cost":600},{"item":"Chicken","quantity":"1kg","estimated_cost":3500},{"item":"Onions","quantity":"2 medium","estimated_cost":200},{"item":"Vegetable oil","quantity":"1/2 cup","estimated_cost":300},{"item":"Seasoning cubes","quantity":"3","estimated_cost":150}]', '["Wash and parboil rice, set aside","Blend tomatoes, peppers and onions","Season and cook chicken until tender","Fry tomato mixture until oil floats","Add rice and chicken stock, stir well","Cover and cook on low heat for 30 minutes"]', 20, 45, 6, 5950, '{"calories":450,"protein":25,"carbs":65,"fats":12,"fiber":3}', '["popular","party-favorite","protein-rich"]', NULL, '2025-09-30T11:04:15.077Z', '2025-09-30T11:04:15.077Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68db64cf5684782e3d7f64b6', 'Eba and Egusi Soup', 'Traditional Nigerian soup made with melon seeds', 'nigerian', 'dinner', '[{"item":"Garri","quantity":"2 cups","estimated_cost":400},{"item":"Egusi (melon seeds)","quantity":"1 cup","estimated_cost":800},{"item":"Ugwu (pumpkin leaves)","quantity":"2 bunches","estimated_cost":200},{"item":"Stockfish","quantity":"200g","estimated_cost":1000},{"item":"Palm oil","quantity":"1/2 cup","estimated_cost":400},{"item":"Crayfish","quantity":"2 tbsp","estimated_cost":300}]', '["Boil stockfish until tender","Blend egusi with peppers","Heat palm oil and fry egusi paste","Add stock and cook for 20 minutes","Add ugwu leaves and cook for 5 minutes","Prepare eba by mixing garri with hot water"]', 15, 35, 5, 3100, '{"calories":520,"protein":22,"carbs":55,"fats":25,"fiber":8}', '["traditional","high-fiber","family-favorite"]', NULL, '2025-09-30T11:04:15.077Z', '2025-09-30T11:04:15.077Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68db64cf5684782e3d7f64b7', 'Beans and Plantain (Ewa Dodo)', 'Nutritious beans paired with sweet fried plantain', 'nigerian', 'lunch', '[{"item":"Beans","quantity":"3 cups","estimated_cost":900},{"item":"Ripe plantains","quantity":"4 large","estimated_cost":800},{"item":"Palm oil","quantity":"1/4 cup","estimated_cost":200},{"item":"Onions","quantity":"1 large","estimated_cost":100},{"item":"Pepper","quantity":"2 tbsp","estimated_cost":100}]', '["Wash and cook beans until soft","Peel and slice plantains","Fry plantains in hot oil until golden","Season beans with palm oil, onions and pepper","Serve hot together"]', 10, 60, 4, 2100, '{"calories":380,"protein":18,"carbs":62,"fats":8,"fiber":12}', '["budget-friendly","high-protein","vegetarian"]', NULL, '2025-09-30T11:04:15.077Z', '2025-09-30T11:04:15.077Z', 'jakomeoyegwa@gmail.com');
INSERT INTO recipes (id, name, description, cuisine_type, meal_type, ingredients, instructions, prep_time, cook_time, servings, estimated_total_cost, nutrition_info, tags, image_url, created_date, updated_date, created_by) VALUES ('68db64cf5684782e3d7f64b8', 'Akara and Pap', 'Popular Nigerian breakfast - bean cakes with corn pudding', 'nigerian', 'breakfast', '[{"item":"Beans (peeled)","quantity":"2 cups","estimated_cost":700},{"item":"Pap (ogi)","quantity":"4 cups","estimated_cost":400},{"item":"Onions","quantity":"1 small","estimated_cost":50},{"item":"Vegetable oil","quantity":"for frying","estimated_cost":400},{"item":"Pepper","quantity":"1 tbsp","estimated_cost":50}]', '["Blend soaked beans with pepper and onions","Whip mixture until fluffy","Heat oil and fry spoonfuls until golden","Prepare pap by mixing with hot water","Serve akara with warm pap"]', 15, 20, 4, 1600, '{"calories":320,"protein":15,"carbs":45,"fats":10,"fiber":6}', '["breakfast","quick","protein-rich"]', NULL, '2025-09-30T11:04:15.077Z', '2025-09-30T11:04:15.077Z', 'jakomeoyegwa@gmail.com');

